# Queries
## JOIN statement
# 1.
SELECT COUNT(S.studentID), U.universityName 
FROM student S
JOIN University U
ON S.universityID = U.universityID
GROUP BY U.universityID;

# 2.
SELECT HP.ProviderID, HP.Name, COUNT(MP.MPID) 
FROM HealthcareProvider HP
JOIN MedicalProfessiONals MP
ON HP.ProviderID = MP.ProviderID
GROUP BY HP.ProviderID;



# ORDER BY statement
# 3.
SELECT InsuranceName, ProviderID, CoverageAmount
FROM Insurance JOIN HealthcareProvider ON Insurance.InsuranceID = HealthcareProvider.InsuranceID
ORDER BY CoverageAmount;

# HAVING statement ## aggregate statement
# 4.
SELECT U.universityName, AVG(SMR.coverageamount)
FROM student S
JOIN University U
ON S.universityID = U.universityID
JOIN StudentMedicalRecords SMR
ON S.studentID = SMR.studentID
GROUP BY U.universityID
HAVING AVG(SMR.coverageamount) > 50000;

#5
SELECT COUNT(providerID), ZipCode FROM HealthcareProvider
GROUP BY ZipCode;



## nested query
#6
SELECT * FROM Student
WHERE StudentID = (SELECT StudentID FROM StudentMedicalRecords WHERE StudentID = 3543);



## all/any query
#7
SELECT R1.ProviderID , R1.Name , R1.Zipcode
FROM HealthcareProvider R1
WHERE R1.ProviderID >= ALL
(SELECT R2.ProviderID
FROM HealthcareProvider R2
WHERE R2.ZipCode = R1.ZipCode);


## exists query
#8
SELECT S.StudentID, S.FirstName, S.LastName ,TEMP.CoverageAmount, TEMP.FileNumber
FROM Student S
JOIN
(
SELECT SM.StudentID, SM.CoverageAmount, SM.FileNumber
FROM StudentMedicalRecords SM
WHERE EXISTS
(
SELECT * FROM Bills B
WHERE SM.FileNumber = B.FileNumber AND  SM.FileNumber = '1721255882'
) 
) TEMP
ON S.StudentID = TEMP.StudentID;



